void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 original_tex = texture(iChannel0, uv).rgb;

    float angle = iTime * 0.09 + 2.0;

    float strength = sin(iTime * cos(iTime * 0.001)) * 0.25 + 0.1;


    for (float i = 0.0; i < 16.0; i++) {
        uv = abs(uv);
        uv -= 0.5;
        uv *= 1.1;
        uv *= mat2(
            cos(angle), -sin(angle),
            sin(angle), cos(angle)
        );
    }


    vec3 tex = texture(iChannel0, uv).rgb;


    fragColor = vec4(mix(original_tex.rgb, tex.rgb, strength), 1.0);
}
