//Using tips from GregRostami in the shaded version
void mainImage( out vec4 O, vec2 U )
{   
    U = 8.*U/iResolution.x - 2.4;
    float e, I = fract(U).y;
    for( O -= O; (O.a+=.1) < 2.; O += vec4(e < .03) )
         e = I * length(U+O.a),
         U *= mat2( sin( (O.a+iTime)*.2 + vec4(0,33,11,0) ) );

}


















