const float pi = 3.14159265358979323846264338327;

float dot2( in vec2 v )
{
    return dot(v, v);
}

// https://www.shadertoy.com/view/clVXWc
float sdfCoolS( in vec2 p )
{
    float six = (p.y<0.0) ? -p.x : p.x;
    p.x = abs(p.x);
    p.y = abs(p.y) - 0.2;
    float rex = p.x - min(round(p.x/0.4),0.4);
    float aby = abs(p.y-0.2)-0.6;

    float d = dot2(vec2(six,-p.y)-clamp(0.5*(six-p.y),0.0,0.2));
    d = min(d,dot2(vec2(p.x,-aby)-clamp(0.5*(p.x-aby),0.0,0.4)));
    d = min(d,dot2(vec2(rex,p.y  -clamp(p.y          ,0.0,0.4))));

    float s = 2.0*p.x + aby + abs(aby+0.4) - 0.4;

    return sqrt(d) * sign(s);
}

float sigmoid( in float x )
{
    return 1. / (1. + exp(x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Speed up
    float t = iTime;
    t *= clamp(iTime, 15., 500.);

    // Normalize screen coordinates from -1 to 1
    vec2 uv  = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 n   = sin(uv*pi);

    float d  = sdfCoolS(n);
    float ad = .2*abs(d);

	// coloring
    vec3 col  = vec3(d*1.2,sigmoid(sin(iTime)*1.5)*.1,ad*.3);
         col  = sin(col);
         //col *= exp(tan(1.5*sin(2.*t*ad)));
         col *= exp(atan(t*ad));
         col /= fract(col.x);

    fragColor = vec4(col, 1.0);
}
