void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv.xy -=.5;
    uv.x *= iResolution.x / iResolution.y;


    float t = iTime;
    vec3 anger = vec3(1., sin(t) * .5 ,cos(t) * .2);
    vec3 calm = vec3 (0.1, .4, 1.);

    float m = sin(t) * .8;
    float y = uv.y - m;
    float wave = smoothstep(y, y -.2, .01);
    float cir = smoothstep(.2,.49,length(uv));
    float pct = abs(sin(t *.3));

    vec3 color = vec3(0.0);
    anger += cir;
    calm += wave;
    color = mix(anger, calm, pct);
    // Output to screen
    fragColor = vec4(color,1.0);
}
