//https://iquilezles.org/articles/palettes/
vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263,0.416,0.557);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    vec2 crossCenter = vec2(0., 0.15);
    vec2 vertRect = vec2(0.075, 0.5);
    vec2 horizRect = vec2(0.35,0.075);
    float d = length(uv-crossCenter);    


    float dist1 = length(max(vec2(0.), abs(uv-crossCenter)-horizRect));
    float dist2 = length(max(vec2(0.), abs(uv)-vertRect));

    float mDist = 1. + 10.0/(2.8*d+(sin(0.2*iTime)));
    // 1./d;


    float v1 = 0.5*sin(12.0*dist1*mDist+8.0*iTime)+0.5;
    float v2 = 0.5*sin(12.0*dist2*mDist+8.0*iTime)+0.5;

    vec3 pcol = palette(min(dist1,dist2)+iTime);

    float v3 = .07/(v1*v2 - 0.7*max(v1,v2) + 0.25); // 3.9*max
    //float v3 = .2/(v1*v2 - 0.2*min(v1,v2) + 0.25)- .02/(v1*v2 - 0.7*max(v1,v2) + 0.25);
    //float v3 = .02/(v1*v2 - max(v1,v2) + 0.5);

    //v3 *= max(0.0,dist1)*max(0.0,dist2); // Add negative cross in middle

    vec3 col = v3*pcol;

    // Output to screen
    fragColor = vec4(col,1.0);
}
