float Xor(float a, float b) {
    return a*(1.-b) + b*(1.-a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.);

    float angle = .78;
    float s = sin(angle);
    float c = cos(angle);
    uv *= mat2(c, -s, s, c);
    uv *= 15.;

    vec2 gv = fract(uv) -.5;
    vec2 id = floor(uv);

    float m = 0.; //smoothstep(.4, .3, d);
    float t = iTime;
    // float dist = length(uv)*30.;

    for(float y=-1.;y<=1.; y++) {
        for(float x=-1.; x<=1.;x++) {
            vec2 offs = vec2(x, y);

            float d = length(gv-offs);
            float dist = length(id+offs)*.3;

            float r = mix(.3, 1.5, sin(-t+dist)*.5+.5);
            m = Xor(m, smoothstep(r, r*.9, d));
            // m += smoothstep(r, r*.9, d);
        }
    }

    col += m;
    // col.rg = gv;
    //col += mod(m, 2.);

    fragColor = vec4(col,1.0);
}
