void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
}

// https://iquilezles.org/articles/distfunctions2d/
float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

vec3 hsv2rgb2(vec3 c, float k) {
    return smoothstep(0. + k, 1. - k,
                      .5 + .5 * cos((vec3(c.x) + vec3(3., 2., 1.) / 3.) * radians(360.)));
}

vec2 pos(int f)
{
    float t = float(f) / 10.;
    vec2 pos = vec2(cos(t) + .3 * sin(t), sin(t)) * 1.2 / exp(.0003 * float(f));

    float t2 = float(f) / 619. * 2.;
    pos = mat2(cos(t2), sin(t2), -sin(t2), cos(t2)) * pos;

    return pos;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 bg = vec3(0.);
    if (iFrame > 1)
        bg = texelFetch(iChannel0, ivec2(fragCoord), 0).rgb;

    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;

    vec2 pos0 = pos(iFrame);
    vec2 pos1 = pos(iFrame + 1);

    float d = sdSegment(uv, pos0, pos1) - .01 / exp(.0001 * float(iFrame));
    float a = smoothstep(-1.5, 1.5, d * iResolution.y);

    fragColor = vec4(mix(bg, hsv2rgb2(vec3(float(iFrame) / 10000., 0, 0), .07), 1. - a), 1.);
}
