float random(vec2 _st){
    return fract(sin(dot(_st.xy, vec2(12.9898,78.233)))*43758.5453123);
}

float noise(vec2 _st){
    vec2 f = fract(_st*5.);
    vec2 id = floor(_st*5.);

    f = f*f*(3.0 - 2.0*f);//smoothstep

    float lb = random(id);
    float rb = random(id + vec2(1., 0.));
    float b = mix(lb, rb, f.x);

    float lt = random(id + vec2(0., 1.));
    float rt = random(id + vec2(1., 1.));
    float t = mix(lt, rt, f.x);

    return mix(b, t, f.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(0.);

    float c = noise(uv*1.0);

    c += noise(uv*2.0)*.5;
    c += noise(uv*4.0)*.25;
    c += noise(uv*16.0)*.125;
    c += noise(uv*32.)*.0125;

    col += sin(distance(vec2(c), vec2(.5))*1.0 + iTime);

    vec4 tex = texture(iChannel0, uv + col.g);

    // Output to screen
    fragColor = vec4(tex);
}
