const int Quality = 70;




vec2 rotate(vec2 uv, vec2 p, float angle)
{
	mat2 rotation = mat2(vec2(cos(angle), -sin(angle)),
						vec2(sin(angle), cos(angle)));
	uv -= p;
	uv = uv * rotation;
	uv += p;
	return uv;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{vec2 uv = fragCoord/iResolution.xy;
    vec4 Color;
    float v;
    vec2 pivot =iMouse.xy/uv.xy;
    vec2 origin = vec2(iMouse.x,iMouse.y)/vec2(iResolution.x,iResolution.y);
    for( float i=0.0;i<1.0;i+=1.0/float(Quality) )
    {


        v = 0.1+i*0.5;//convert "i" to the 0.9 to 1 range
        Color += texture(iChannel0,rotate(uv,origin, v));  //
        Color += texture(iChannel0,rotate(uv,origin, v/-1.0)); 
    }
    Color /= float(Quality*2);

    fragColor =  Color ;
}


