#define HPI 1.57079632679
#define PI 3.14159265359
#define TAU 6.28318530718
#define sat(x) clamp(x, 0.0, 1.0)

float blackbody(float v, float t)
{
    return 2.*v*v*v*(1./(exp(v/t) - 1.));
}

float responseCurve(float w, float p, float v)
{
    return sin(TAU*sat((v-p)/w + 0.5) - HPI)*0.5+0.5;
}

vec3 eyeResponsivity(float v)
{
    return vec3(responseCurve(100., 440., v),
                responseCurve(200., 550., v),
                responseCurve(200., 600., v)
                );
}

vec3 getBlackbodyColor(float steps, float t)
{
    vec3 col = vec3(0);
    vec3 m = vec3(0);
    float stInc = 300./steps;
    for(float v = 400.; v < 700.; v += stInc)
    {
        vec3 r = eyeResponsivity(v);
        m += r;
        col += blackbody(v, t)*r*vec3(0.11, 0.59, 0.3);
    }
    return col/m;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = vec4(sqrt(getBlackbodyColor(20., uv.x*50.+15.) / (1.0 + getBlackbodyColor(20., uv.x*50.+15.))),1.0);
}
