const int MAX_ITERATIONS = 512;
vec2 getCoord() {
    return vec2(texelFetch(iChannel0,ivec2(0, 0), 0).r,texelFetch(iChannel0,ivec2(1,0),0).r);
}
float getZoom() {
    if(texelFetch(iChannel0, ivec2(2, 0), 0).r > -1.0){
        return texelFetch(iChannel0,ivec2(2,0),0).r;
    }
    else {
        return -0.5;
    }
}
int getzoomthingy() {
    return int(texelFetch(iChannel0, ivec2(3, 0), 0).r);
}
vec2 coord;
int fractal(vec2 c, vec2 z) {
  for (int iteration = 0; iteration < MAX_ITERATIONS; iteration++) {

    float real = abs(z.x) * abs(z.x) - abs(z.y) * abs(z.y) + c.x;
    float imaginary = -2.0 * abs(z.x) * abs(z.y) + c.y;

    z.x = real;
    z.y = imaginary;

    if (length(z) > 2.0) {
      return iteration;
    }
  }

  return 0;
}

int mandelbrot(vec2 frag) {
  vec2 c = vec2(frag.x, frag.y);
  vec2 z = vec2(0.0, 0.0);

  return fractal(c, z);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord){

    float zoom = 0.25 * pow(1.03, getZoom());
    vec2 coordRatio = (fragCoord / zoom) / iResolution.xy;
    vec2 getActualCoords = vec2(getCoord().x * (iResolution.x / iResolution.y), getCoord().y);
    coordRatio -= 0.5 / zoom;
    coordRatio.x *= (iResolution.x / iResolution.y);
    vec2 coords = coordRatio + (getActualCoords / iResolution.xy);
    vec2 z = vec2(0.0, 0.0);
    vec2 c = coords;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    float color = (5.0 * float(mandelbrot(coords)) / float(mandelbrot(coords)+256));
    fragColor = vec4(color, color, color, 1.0);    
}

