const int KEY_A = 65;
const int KEY_W = 87;
const int KEY_D = 68;
const int KEY_S = 83;
const int KEY_LEFT = 37;
const int KEY_UP = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN = 40;
const int KEY_Q = 81;
const int KEY_E = 69;
const int KEY_R = 82;
const float speed = 350.0;
void mainImage(out vec4 fragColor, in vec2 fragCoord){
    float zoom=0.25*pow(1.03,getZoom());
    float outData=0.0;
    switch(int(fragCoord.x)) {
        case 0:
        	outData = texelFetch(iChannel0, ivec2(0), 0).r +
                (iTimeDelta * speed / zoom) * texelFetch(iChannel1, ivec2(KEY_RIGHT, 0), 0).r -
                (iTimeDelta * speed / zoom) * texelFetch(iChannel1, ivec2(KEY_LEFT, 0), 0).r +
                (iTimeDelta * speed / zoom) * texelFetch(iChannel1, ivec2(KEY_D, 0), 0).r -
                (iTimeDelta * speed / zoom) * texelFetch(iChannel1, ivec2(KEY_A, 0), 0).r;
        	break;

        case 1:
        	outData = texelFetch(iChannel0, ivec2(1, 0), 0).r +
                (iTimeDelta * speed / zoom) * texelFetch(iChannel1, ivec2(KEY_UP, 0), 0).r -
                (iTimeDelta * speed / zoom) * texelFetch(iChannel1, ivec2(KEY_DOWN, 0), 0).r +
                (iTimeDelta * speed / zoom) * texelFetch(iChannel1, ivec2(KEY_W, 0), 0).r -
                (iTimeDelta * speed / zoom) * texelFetch(iChannel1, ivec2(KEY_S, 0), 0).r;
        	break;
        case 2:
            if(texelFetch(iChannel0, ivec2(2,0),0).r > -1.0){
                outData = texelFetch(iChannel0,ivec2(2, 0), 0).r +
                iTimeDelta * speed * 0.125 * texelFetch(iChannel1, ivec2(KEY_E, 0), 0).r -
                iTimeDelta * speed * 0.125 * texelFetch(iChannel1, ivec2(KEY_Q, 0), 0).r;
            }
            else{
                outData = 0.0;
            }
            break;
    }
    fragColor = vec4(outData,0.0,0.0,1.0);
}
