float hash(vec2 n) {
    return abs(fract(72.53 * sin((n.x/7654. + 7.0) * 315.0 + 127.0) * cos((n.y/9876.+11.0) * 513.0) * 7759.0));
}

const float n = 15.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = vec2(iResolution.x, iResolution.y);
    vec2 uv = fragCoord;

    uv.x=pow(uv.x/res.x,0.70)*res.x;

    uv.x+=iTime*60.0;


    vec2 step = floor(vec2(res.x, res.y)/n);

    float md = length(step);

    vec2 crn[4];
    crn[0] = floor(uv/step)*step;
    crn[3] = ceil(uv/step)*step;
    crn[1] = vec2(crn[3].x,crn[0].y);
    crn[2] = vec2(crn[0].x, crn[3].y);
    //{tl, tr, bl, br};
    float clr[4];
    for(int i=0;i<4;++i) {
        clr[i] = 0.25+hash(crn[i])/2.;
    }
    float c = 0.;
    const float m =0.8;
    for(int i=0;i<4;++i) {
        c = max(c,clr[i] * (pow(md,m)-pow(length(crn[i]-uv),m)));
    }
    c /= pow(md,m) * 2.;

    vec3 col = vec3(2.6,2.,0.2)*c;

    fragColor = vec4(col,1.0);
}
