void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 正規化されたピクセル座標
    vec2 uv = fragCoord / iResolution.yy;

    // 時間パラメータ
    float t = iTime;

    // FMシンセサイズ
    vec3 fm = (1.5 + sin(3.14 * uv.xyx + t + 30.0 * sin(3.0 * 3.14 * uv.xyx)));

    // グラデーション色
    vec3 grad = 0.5 + 0.5 * cos(3.14 * (uv.xyx + t / 4.0));

    // FMとグラデーションの積
    vec3 col = fm * grad;

    // 色を出力
    fragColor = vec4(col,1.0);
}
