float plot(vec2 uv, float y) {
    return (0.00, 0.01, abs(uv.y - y));
}

void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float y = -0.75 * abs(sin(3.14 * uv.x + iTime));
    y = cos(y) + 0.1 * sin(iTime) + 0.6;

    vec3 colour = vec3(y);

    float ptc = plot(uv, y);
    colour = (vec3(1.0, 1.0, 0.0) - vec3(ptc)) * colour + ptc * vec3(0.000,0.749,1.000);
    colour = abs(colour);


    // Output to screen
    fragColour = vec4(colour, 1.0);
}
