vec4 scrollGradient (vec4 start, vec4 end, float offset, float width) {
    return start + (end - start) * offset / width;
}

vec4 start = vec4(1.0, 0.2, 0.0, 1.0);
vec4 end = vec4(0.0, 0.2, 0.8, 0.0);
void mainImage (out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    float width = 1.5;
    float offset = (width - 1.0) * iMouse.x / iResolution.x;

    fragColor = scrollGradient(start, end, uv.x + offset, width);
}
