float vortex(vec2 x, float leaves, float spin, float phase) 
{
    float r = length(x) - smoothstep(0.0, 1.0, phase);
    float a = atan(x.y, x.x) + smoothstep(0.0, 1.0, phase);
    return r - 1.0 + 0.1 * sin((leaves * a + leaves * spin * r * r));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float t = sin(iTime * 4.0) + 0.25;
    float v = vortex(uv * 3.0, 8.0, 0.35, t);
    float d = 1.0 - smoothstep(0.0, 1.0, v * 0.6);
    vec3 col = vec3(0.0, 0.5, 1.0) * d;
    col -= abs(v);
    fragColor = vec4(col,1.0);
}
