#define M(a) mat2(cos(a+vec4(0,11,33,0)))//
// SDF cuboid
#define D(a) (length(max(e,abs(p)- a))-.5)//


void mainImage(out vec4 O, vec2 v)
{
        vec2 R = iResolution.xy,

             u =(v + v - R) / R.y;

        float s,t=iTime , d; // d=9.;

        vec3 o,r,e,n,p;//,C;

        e.y =1e-2;

        o.z = -20.;  

        r=vec3(u,1.);

        for(int i,j;i++<40;s += n[i % 3] = d++) //d++)
        {
                e=e.yzx;

                p = o + r * s + e;

                p.xy *=M(t/3.);p.xz*=M(t*2.);p.yz *= M(t);


                //d=length(  (sin(p.yzx)*sin(p)-.08))-.1;
                // Exchange p.xyz
                //d=6.;
                //for(j=0,d=6.; j++<3; p=p.yzx) // 正常漫射
                //for(d*=.85,j=0;j++<13;p=p.yzx) // 假雾
                for(j=0;j++<3;p=p.yzx) // 假镜面

                     // square ring 方环
                     d=min(d,max(D(vec3(9,5,1)),-D(vec3(7,3,9)))) ;   // ring            
        }

        O = O * 0. + .3 + dot(o,n) ;    
}
