// simple rgb circle thing idk im bored

#define SPEED iTime * 3.
#define LINE_THICKNESS 0.9
#define ZOOM 4. * 25.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy - 0.5;
    uv *= iResolution.x / iResolution.y;

    vec3 col = 0.5 + 0.5*cos(SPEED + ZOOM / 5. * length(uv.xyx)+vec3(0,2,4));

    col = clamp(smoothstep(LINE_THICKNESS, 1.0, col), 0.0, 1.0); 

    fragColor = vec4(col*col, 1.0);

}
