void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= (iResolution.x / iResolution.y);

    uv *= 1.5;
    //uv.x = sin(uv.x + iTime * 0.2);
    //uv.y = cos(uv.y - iTime * 0.3);

    uv = vec2(length(uv), atan(uv.y / uv.x) * 3.141592);

    vec3 color = vec3(0.0);

    float fxr = uv.x * (uv.x -sin(uv.x));
    float fxg = uv.x * (uv.x + cos(uv.y + iTime));
    float fxb = uv.x * (uv.x * cos(uv.x + iTime * 1.2));

    vec2 fxparamr = vec2(uv.x, fxr);
    vec2 fxparamg = vec2(uv.x, fxg);
    vec2 fxparamb = vec2(uv.x, fxb);

    vec2 pr = vec2(sin(iTime) / 2.0, sin(uv.x / 2.0));
    vec2 pg = vec2(sin(iTime) / 2.0, cos(uv.x / 3.0));
    vec2 pb = vec2(sin(iTime) / 2.0, pow(uv.x, 2.0));

    float distr = distance(pr, fxparamr);
    float distg = distance(pg, fxparamg);
    float distb = distance(pb, fxparamb);

    vec2 p2r = vec2(sin(uv.x + iTime / 2.0), cos(uv.y - uv.x));
    vec2 p2g = vec2(sin(uv.x), cos(uv.y));
    vec2 p2b = vec2(sin(uv.x + iTime / 3.0), cos(uv.y + uv.x));

    vec2 vp2r = distr - p2r;
    vec2 vp2g = distg - uv;
    vec2 vp2b = distb - p2b;

    float dist2r = length(vp2r * 2.0);
    float dist2g = length(vp2g * 1.2);
    float dist2b = length(vp2b);

    color = vec3(sin((dist2r + iTime)), cos((dist2g + iTime)), tan(dist2b + iTime)); 
    color.r = pow(color.r, 3.0);
    color.g = pow(color.g, 4.0);
    color.b = pow(color.b, 5.0);
    fragColor = vec4(color, 1.0);

}
