// uncomment to try as blur (mouseY changes intensity)
//#define blur

// ratios
float g1 = 0.754877666; // (one over plastic number)
float g2 = 0.569840291; // (one over plastic number squared)

// inverted ratios
float ig1 = 0.245122334; // 1.-g1
float ig2 = 0.430159709; // 1.-g2

float LDG(float pixelX, float pixelY, float a, float b, float seed)
{
    return fract(a*pixelX + b*pixelY + seed);
}

float scale = 6.; //#ifdef blur sets the scale to 1.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    #ifdef blur
    scale = 1.;
    #endif
    vec2 uv = (fragCoord-.5*iResolution.xy);
    vec2 UV = floor(uv/scale);
    uv = fragCoord/iResolution.xy;

    vec3 pattern = vec3(0.);
    float seed = fract(float(iFrame)*sqrt(2.)); //change over time
    pattern.r = LDG(UV.x, UV.y,  g1, g2, seed);
    pattern.g = LDG(UV.y, UV.x, ig1, g2, seed);
    pattern.b = LDG(UV.x, UV.y,  g1, ig2,seed);

    vec3 col = pattern;
    #ifdef blur
    col = texture(iChannel0, uv + (pattern.xy-.5)*exp2(0.-iMouse.y/iResolution.y*9.)).rgb;
    #endif
    fragColor = vec4(col,1.0);
}
