void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy) / iResolution.y;
    vec2 p = uv;
    float ss = .75/iResolution.y;

    vec3 bg = vec3(.133,.153,.188);
    vec3 c1 = vec3(.298,.667,.702);
    vec3 c2 = vec3(.224,.243,.275);

    vec3 col = abs(p.y) < .25 ? c1 : bg;   
    col = mix(bg, col, smoothstep(.65-ss, .65, abs(p.x) + abs(p.y)));
    col = mix(c1, col, smoothstep(.4-ss, .4, abs(p.x) + abs(p.y)));
    col = mix(c2, col, smoothstep(.1-ss, .1, length(p)));

    fragColor = vec4(col,1.0);
}
