float fire( vec2 p ) {

	float f=1., s=1.6;
    p.y -= iTime*4.;
	vec2 r = vec2(0);	
    for(int i = 0;i<15;i++){	
		r += sin( p*f +iTime*2.5)/f;
        p *= mat2(.8,.6,-.8,.6);
        p += r*.4;
	    f *= s;
	}
	return length(r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = 8.*fragCoord/iResolution.xy-4.;
    p.x *= iResolution.x/iResolution.y;

    float f = fire(p);
    float r = (fire(p+.1)-.5*f)*exp(-p.y*.2-1.);

    fragColor = r*vec4(f*f, f, 1, 1);
}
