#define PI 3.14159265359
#define coord fragCoord/iResolution.xy
#define recoord iResolution.x/iResolution.y

vec3 white = vec3 (1.0, 1.0, 1.0);
vec3 black = vec3 (0.0, 0.0, 0.0); 
vec3 red = vec3 (0.75, 0.0, 0.0); 
vec3 green = vec3 (0.0, 0.5, 0.0); 
vec3 yellow  = vec3 (0.85, 0.85, 0.0); 
vec3 brown = vec3 (0.5, 0.3, 0.0);
vec3 eyeBrown = vec3 (0.3, 0.1, 0.0);
vec3 orange = vec3 (1.0, 0.5, 0.0);

vec2 uv[4] = vec2[4](vec2(0,0), vec2(0,0), vec2(0,0), vec2(0,0));

float spawnBox(vec2 uv, float x, float y, float w, float h)
{ 
    vec2 bl = step(vec2(x, y), uv);
    vec2 tr = step(1.0 - vec2(x, y) - vec2(w, h), 1.0 - uv);
    float pct = bl.x* bl.y * tr.x * tr.y; 
    return pct;
}

float spawnCircle(vec2 uv, float x, float y, float radius)
{
    vec2 dist = uv-vec2(x,y);
    return 1.0 - smoothstep(radius - radius * 0.015, radius + radius * 0.01, dot(dist,dist) * 1.0);
}

mat2 rotate2d(float _angle)
{    
return mat2(cos(_angle),-sin(_angle), sin(_angle),cos(_angle));
}

float spawnTriangle(vec2 st, float scale)
{
    st = st * scale - 0.0;
    int N = 3;
    float a = atan(st.x,st.y)+PI;
    float r = (2.0 * PI)/float(N);
    float d = cos(floor(.5+a/r)*r-a)*length(st);
    float triangle = 1.0-smoothstep(.4,.41,d);
    return triangle; 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec3 col = vec3(0.0);   

    for (int i = 0; i < 4; i++)
    {
        uv[i] = coord; 
        uv[i].x *= recoord; 
    }  

    //whiteEye
    float whiteEye = spawnCircle(uv[0], 1.09, 0.4, 0.0005); 
    whiteEye += spawnCircle(uv[0], 1.413, 0.4, 0.0005); 

    //brownEyes
    uv[1].x *= recoord * 0.7; 
    float brownEyes = spawnCircle(uv[1], 1.368, 0.386, 0.002); 
    brownEyes += spawnCircle(uv[1], 1.77, 0.386, 0.002); 
    brownEyes *= 1.0 - whiteEye; 

    //blush
    vec2 bc = coord; 
    bc.x *= recoord * 0.8; 
    float blush = spawnCircle(bc, 0.79, 0.25, 0.002);
    blush += spawnCircle(bc, 1.21, 0.245, 0.0025);

    //mouth
    float mouth = spawnCircle(bc, 1.005, 0.19, 0.0025);
    mouth += spawnCircle(bc, 1.004, 0.194, 0.0025);
    mouth += spawnCircle(bc, 1.009, 0.194, 0.0025);
    mouth += spawnCircle(bc, 1.010, 0.185, 0.0025);
    mouth = clamp(mouth, 0.0, 1.0); 

    //nose
    vec2 nc = coord; 
    nc.x *= recoord; 
    nc.y *= recoord; 
    nc.y *= recoord * 0.7; 
    float nose = spawnCircle(nc, 1.235, 0.692, 0.0008);

    //pikaBody
    uv[2] -= vec2(1.025, 0.035);
    uv[2] *= rotate2d(0.25);
    uv[2] += vec2(1.03, 0.05);
    float pikaBody = spawnBox(uv[2], 0.98, 0.0, 0.1, 0.1); 

    vec2 sv = coord; 
    sv.x *= recoord;      
    sv -= vec2(1.63, 0.07);
    sv *= rotate2d(-0.14);
    sv += vec2(0.05, 0.125);    
    pikaBody += spawnBox(sv, 0.0, 0.0, 0.1, 0.25); 

    vec2 sv2 = coord; 
    sv2.x *= recoord;      
    sv2 -= vec2(1.58, 0.3);
    sv2 *= rotate2d(-0.28);
    sv2 += vec2(0.05, 0.125);    
    pikaBody += spawnBox(sv2, 0.0, 0.0, 0.1, 0.25); 

    pikaBody += spawnCircle(uv[0], 1.538, 0.43, 0.003);

    vec2 uf1 = coord; 
    uf1.x *= recoord;      
    uf1 -= vec2(1.01, 0.1);
    uf1 *= rotate2d(-0.4);
    uf1 += vec2(0.05, 0.125);    
    pikaBody += spawnBox(uf1, 0.0, 0.0, 0.1, 0.25); 

    pikaBody += spawnCircle(uv[0], 1.01, 0.215, 0.01);

    vec2 uf2 = coord; 
    uf2.x *= recoord;      
    uf2 -= vec2(0.995, 0.35);
    uf2 *= rotate2d(0.28);
    uf2 += vec2(0.05, 0.125);    
    pikaBody += spawnBox(uf2, 0.0, 0.0, 0.1, 0.25);  

    pikaBody += spawnBox(uv[0], 1.05, 0.0, 0.58, 0.25);  
    pikaBody += spawnBox(uv[0], 1.02, 0.2, 0.55, 0.38);
    pikaBody += spawnCircle(nc, 1.28, 1.075, 0.08);

    //pikaEars
    vec2 pk1 = coord; 
    pk1.x *= recoord;      
    pk1 -= vec2(0.97, 0.54);
    pk1 *= rotate2d(-0.6);
    pk1 += vec2(0.025, 0.0625);    
    float pikaEars = spawnBox(pk1, 0.0, 0.0, 0.05, 0.34);  
    pikaEars += spawnBox(pk1, 0.02, -0.1, 0.09, 0.34);

    vec2 pk2 = coord; 
    pk2.x *= recoord;      
    pk2 -= vec2(0.818 , 0.8);
    pk2 *= rotate2d(-0.6);
    pk2 += vec2(0.0, 0.0);    
    pikaEars += spawnTriangle(pk2, 15.0); 

    vec2 pk3 = coord; 
    pk3.x *= recoord;      
    pk3 -= vec2(1.07, 0.6134);
    pk3 *= rotate2d(-0.9);
    pk3 += vec2(0.025, 0.0625);    
    pikaEars += spawnBox(pk3, 0.0, 0.0, 0.05, 0.34);  
    pikaEars += spawnBox(pk3, -0.04, 0.01, 0.08, 0.34);  

    vec2 pk4 = coord; 
    pk4.x *= recoord;      
    pk4 -= vec2(1.6, 0.52);
    pk4 *= rotate2d(0.75);
    pk4 += vec2(0.025, 0.0625);    
    pikaEars += spawnBox(pk4, 0.0, 0.0, 0.05, 0.34);  
    pikaEars += spawnBox(pk4, -0.05, -0.1, 0.05, 0.34);  

    vec2 pk5 = coord; 
    pk5.x *= recoord;      
    pk5 -= vec2(1.47, 0.6);
    pk5 *= rotate2d(1.18);
    pk5 += vec2(0.025, 0.0625);    
    pikaEars += spawnBox(pk5, 0.0, 0.0, 0.05, 0.5);
    pikaEars += spawnBox(pk5, 0.03, -0.18, 0.067, 0.5);

    float pikachu = (pikaEars + pikaBody);  

    //wood
    vec2 wd1 = coord; 
    wd1.x *= recoord;      
    wd1 -= vec2(1.18, 0.5);
    wd1 *= rotate2d(-0.23);
    wd1 += vec2(0.125, 0.75);    
    float wood = spawnBox(wd1, 0.0, 0.0, 0.25, 1.5); 
    wood += spawnBox(wd1, 0.21, 0.0, 0.25, 1.5); 

    vec2 wd2 = coord; 
    wd2.x *= recoord;      
    wd2 -= vec2(0.32, 0.5);
    wd2 *= rotate2d(0.34);
    wd2 += vec2(0.125, 0.75);    
    wood += spawnBox(wd2, 0.0, 0.0, 0.25, 1.5); 
    wood += spawnBox(wd2, 0.21, 0.0, 0.25, 1.5); 
    wood *= 1.0 - pikachu; 
    wood = clamp(wood, 0.0, 1.0); 

    float background = spawnBox(uv[0], 0.0, 0.0, 2.0, 1.1); 
    background *= 1.0 - (pikachu + wood);
    background = clamp(background, 0.0, 1.0); 

    pikachu *= (1.0 - (whiteEye + brownEyes + blush + mouth + nose));
    pikachu = clamp(pikachu, 0.0, 1.0); 

    vec3 pikachuAll = pikachu * yellow; 
    vec3 blushAll = blush * red; 
    vec3 mouthAll = mouth * orange; 
    vec3 noseAll = nose * brown; 
    vec3 whiteAll = whiteEye * white; 
    vec3 brownAll = brownEyes * eyeBrown;    
    vec3 woodAll = wood * brown; 
    vec3 bg = background * green; 

    col = pikachuAll + blushAll + mouthAll + noseAll + whiteAll + brownAll + woodAll + bg; 

    // Output to screen
    fragColor = vec4(col,1.0);
}
