#define PI 3.14159

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float aspectRatio = iResolution.x / iResolution.y;
    vec2 uv = fragCoord.xy / iResolution.xy + -0.5;
    uv.x *= aspectRatio;
    uv /= 1.5;

    float s = sin(0.5);
    float c = cos(0.5);
    float dist = length(uv);

    float a = atan(uv.y, uv.x);
    a -= dist*(24.0*s*3.0*c*1.5) + a * sin(sin(iTime*0.5)*1.5);

    float t = abs(mod(a, PI) - PI * 0.5) / (PI * 1.0);
    t = pow(t, 0.0) + uv.x*uv.y + cos(a * 1.0);

    vec3 color = mix(vec3(0.0, 0.0, 0.5), vec3(0.5, 1.0, 0.5), t*t);
    color = vec3(2.5 * 0.8, 1.0, 0.5)*t/2.0;

    fragColor = vec4(color, 1.0);
}
