void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    const float fliphz = 12.0;
    const float onOffSecs = 16.0;

    bool onOffState = pow(cos(iDate.w/onOffSecs*2.0), 2.0) < 0.5;
    if (onOffState) {
        fragColor = vec4(0.0);
        return;
    }

    vec2 coordFromCenter = iResolution.xy/2.0 - fragCoord.xy;
    float r = length(coordFromCenter);
    float theta = atan(coordFromCenter.x / coordFromCenter.y);

    bool radialState = sin(r/12.0) < 0.0;
    bool thetaState = pow(cos((6.0*theta) + 1.0*iTime), 2.0) < 0.5;
    bool state = thetaState ^^ radialState;

    bool flipState = mod(fract(iDate.w)*fliphz/2.0, 1.0) < 0.5;
    if (flipState) {
        state = !state;
    }
	fragColor = vec4(float(state));
}
