//golfed by Fabrice
#define C(x) cos(x+vec2(0,11))

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( u - .5*R ) / R.y;      

    float t = iTime, PI = 3.14, i=0.;
    for ( ; i++< 40.; U *= 1.16 )
        U  =  ( abs(U) - .65 - sin(t*PI/1e2)/2. )
             * mat2(cos(t*.1+vec4(0,11,33,0)));

    O.rgb = abs( U* mat3x2( C(t             ),
                            C(t* .11 - PI/3.),
                            C(t*1.11 + PI/3.)
                )         );
}
