void mainImage( out vec4 o, in vec2 FC )
{
#define R(a) mix(q*dot(p,q),p,cos(a))+sin(a)*cross(p,q)
float i,j,d=.1,e; vec3 p,q=vec3(.577);
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e)*2.5;
    p=vec3(p.x*p.x+p.y*p.y, 2.*p.x*p.y,p.z);
    p=R(iTime);

    //p*=rotate3D(t,vec3(-1));
    for(j=0.;j++<4.;)
        p=abs(abs(fract(p))-.5)-.2,
        d=pow(dot(p,p),3.5);

    e+=exp(d);
}o+=vec4(p,1)*5./i;
}
