#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

vec3 bcol(vec2 uv){
    uv = fract((uv)*10.)-.5;
    float d = (length(uv.x*uv.y)-.5);
    d = smoothstep(1.,0.,d*d);
   return (d*d*vec3(.4,.23,.7))*4.2;
}
//ball from 
//https://www.shadertoy.com/view/DdyBRd
//https://www.shadertoy.com/user/tholzer
vec3 ball(vec2 uv, float raza){
    uv.y -= .3;
    vec2 offs;
    offs.y +=cos(sin(iTime/2.)*2.)*3.5;
    offs.x +=cos(sin(iTime))*2.;
    vec3 col;
    float rr = dot(uv/2., uv/2.);
    float t = abs(raza - rr);
	float z = sqrt(t*30.);
    if (rr<raza) { 
        uv *= 1.5 - z;
        col -=  bcol(uv + offs * .5);
        col = sqrt(col  *col *t )*5.;
    }
    return col;
}

vec3 fog(vec2 uv){
    uv.y += .2*sin(cos(iTime));
    return vec3(.5,.3,.7)*( length(uv)-.695)*1.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -.5* iResolution.xy)/iResolution.y;
    uv *= rot(  iMouse.z > 0. ? -iMouse.x/90. : sin( cos( iTime/10. ) ) );
    vec2 uv2 = uv;
    float rr = sin(cos(iTime)/30.)/3.+.002;
    uv *= pow(atan(uv.x,uv.y*2.)*3.,2.);
    uv = vec2(uv.y*3., 2. )/uv.x;
    uv.y +=cos(sin(iTime/2.)*2.)*.5;
    uv.x +=cos(sin(iTime))*.5;
    uv = fract(uv*10.);
    float d = abs(uv.y-rr*32.);
    uv2.y += sin(cos(iTime)/8.);
    d *= pow(abs(uv2.x),3.-rr);
    vec3 col = vec3(d/.3*vec3(.5,.3,1.));

    fragColor = vec4(max(col,ball(uv2,.02+rr)/1.5
                     +ball(uv2,.04125+rr*2.)/2.5)
                    -fog(uv2)
                    ,1.0);
}
