#define aa 2./iResolution.y/2.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = (fragCoord - iResolution.xy*.5)/iResolution.y; // normalize space
    vec2 m = (iMouse.xy - iResolution.xy*.5)/iResolution.y;

    // Time varying pixel color    
    vec2 eyePos1 = vec2(-0.2,0.);
    vec2 irisPos1 = -1.*normalize(eyePos1-m)*0.06;
    float eyeMask1 = smoothstep(0.+aa, 0.-aa, distance(eyePos1, p)-0.1);
    eyeMask1 -= smoothstep(0.+aa, 0.-aa, distance(irisPos1+eyePos1, p)-0.05);


    vec2 eyePos2 = vec2(0.2,0.);
    vec2 irisPos2 = -1.*normalize(eyePos2-m)*0.06;
    float eyeMask2 = smoothstep(0.+aa, 0.-aa, distance(eyePos2, p)-0.1);
    eyeMask2 -= smoothstep(0.+aa, 0.-aa, distance(irisPos2+eyePos2, p)-0.05);

    //v = length(p);


    // Output to screen
    vec3 col = vec3(eyeMask1+eyeMask2);
    fragColor = vec4(col,1.0);
}
