#ifdef GL_ES
precision mediump float;
#endif
vec3 draw_line(vec2 p1, vec2 p2, vec2 st, vec3 bc, vec3 sc){
    vec2 w=st-p2;
    vec2 u=p1-p2;
    float s=clamp(dot(w,u)/dot(u,u),0.,1.);
    bc=mix(bc,sc,smoothstep(2./iResolution.y,0.,length(w-s*u)));
    return bc;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st=(fragCoord.xy - .5*iResolution.xy )/iResolution.y;
	vec2 mouse=( iMouse.xy.xy - .5*iResolution.xy )/iResolution.y;
    vec2 c=vec2(-.5,0.);
    vec2 v=normalize(vec2(1.,0.));
	vec2 project=dot(mouse,v)*v;
    vec3 color=vec3(1.);
    color=draw_line(c,v+c,st,color,vec3(0.));
    color=draw_line(c,project,st,color,vec3(1.,0.,0.));
    color=draw_line(c,mouse,st,color,vec3(0.,1.,0.));
    fragColor=vec4(color,1.);
}
