#define PI 3.14159265

vec2 rotate(vec2 v, float a) {

    return vec2(v.x * cos(a) - v.y * sin(a), v.x * sin(a) + v.y * cos(a));

}

vec3 hsv2rgb(vec3 c) {

    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);

    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);

    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);

}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = (fragCoord.xy / iResolution.y) * 2.0 - 1.0;

    vec2 p = rotate(uv, iTime * 0.1);

    p *= 0.5 + 0.5 * sin(iTime * 0.5);

    float r = length(p);

    float a = atan(p.y, p.x) / PI + iTime * 0.25;

    float f = cos(r * 20.0 - iTime * 2.0);

    vec3 hsv = vec3(a, r, 0.5 + 0.5 * f);

    vec3 col = hsv2rgb(hsv);

    col = mix(col, vec3(1,1,1), pow(length(uv), 2.0));

    fragColor = vec4(col, 1.0);

}
