void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    uv *= sin(1.0+iTime) ; 
    vec2 gv = fract(uv);
    vec3 col = vec3(0);
    if (gv.x > 0.98 || gv.y > 0.98) col = vec3(1, 0, 0);
    fragColor = vec4(col, 1.0);
}
