vec2 water_flow = vec2(0.05, 0.1);
float water_scaling = 2.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 water_effect_uv = water_flow * iTime + uv;
    water_effect_uv.x += 0.1 * sin( uv.y * 6.28318530718 + iTime);

    float scaling = texture(iChannel1, water_effect_uv * water_scaling).x;

    fragColor = texture(iChannel0, uv * scaling);
}
