#define t mod(iTime, 8.0)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord /iResolution.xy;
    vec2 st =  (uv - 0.5)* (pow(10.0, t));

    vec3 col = vec3(r(st));
    if(uv.x > 0.5){col.x = rand(st);}
    if(abs(0.5 - uv.x) < 0.005){col = vec3(0);}
    // Output to screen
    fragColor = vec4(col.xxx,1.0);
}

float r (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float rand(vec2 st){
    return r( vec2(1.0 / (0.1 + r(st)), r(st.yx)));
}
