void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 fragCoords = fragCoord.xy / iResolution.xy;

    vec3 ColorA = vec3(1,0,0);
    vec3 ColorB = vec3(0,0,1);
    vec3 ColorC = vec3(0.5,0.5,0.5);
    vec3 FinalColor;

    if(fragCoords.x < 0.5)
    {
        FinalColor = ColorA;
    }
    else
    {
        FinalColor = ColorB;
    }

    if(fragCoords.y < 0.5)
    {
        FinalColor = FinalColor + ColorC;
    }

    fragColor = vec4(FinalColor.x, FinalColor.y, FinalColor.z, 1.0);
}
