#define res iResolution
#define t iTime
#define pi 3.14159

vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

float r(float a){
    return random2(vec2(a, 3.14159)).x;
}

vec2 randcis(vec2 seed, float time){
    vec2 ra = random2(seed);
    vec2 rb = random2(ra + seed);
    vec2 rc = random2(rb + seed);
    return vec2(ra.x * sin(pi * (rb.x * time + rc.x)),
                ra.y * cos(pi * (rb.y * time + rc.y)));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/res.y;
    vec2 st = uv - vec2(res.x/res.y, 1.0) * 0.5;
    st *= 25.0;
    vec2 cor = floor(st);

    float minda =  999.0;
    float mindb = 1000.0;
    float mindc = 1001.0;
    for(int x = -2; x < 3;x++){
        for(int y = -2; y < 3; y++){
            vec2 test = cor + vec2(float(x), float(y));
            vec2 centre = test + random2(test) + randcis(test, t);
            float dist = length(centre - st);
            if(dist < minda){
                mindc = mindb;
                mindb = minda;
                minda = dist;
            }else{
                if(dist < mindb){
                    mindc = mindb;
                    mindb = dist;
                }
                else{
                    if(dist < mindc){
                        mindc = dist;
                    }
                }
            }
        }
    }


    // Time varying pixel color
    vec3 col = vec3(1.0);

    if(mindc - mindb < (20.0 * (1.0 + sin(t * pi * 0.3)) - length(st))* 0.01 ){
        col = vec3(0.0);
    }

    fragColor = vec4(col,1.0);
}
