void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
    float r = length(uv);
    float a = atan(uv.y, uv.x);
    float t = iTime * 0.3;
    float f = smoothstep(0.35, 0.05, r) * (25.0 + sin(10.0 * (a + t)));
    fragColor = vec4(f, f, f, 1.0);
}
