void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (2.0 * fragCoord - iResolution.xy) / min(iResolution.y, iResolution.x);
    vec2 uv = p * 2.0;

    float time = iTime * 0.5;

    // Create underwater scene
    vec3 waterColor = vec3(0.106, 0.561, 0.835);
    vec3 sandColor = vec3(0.953, 0.882, 0.737);
    vec3 color = mix(sandColor, waterColor, smoothstep(-0.2, 0.2, uv.y));

    // Add shimmering light rays
    vec2 lightPos = vec2(-0.3, 0.5);
    vec2 toLight = normalize(lightPos - uv);
    float lightIntensity = max(0.0, dot(vec2(0.0, 1.0), toLight));
    float rays = pow(lightIntensity, 10.0) * 0.1;
    color += vec3(1.0, 0.9, 0.5) * rays;

    // Add gentle waves
    float waves = sin(uv.x * 10.0 + time) * 0.02 + sin(uv.y * 25.0 + time) * 0.02;
    color += vec3(0.05, 0.1, 0.2) * waves;

    fragColor = vec4(color, 1.0);
}
