void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    // Time varying pixel color
    vec3 col = 1.0 + cos(iTime+uv.xyx+vec3(0,2,4));

    float r = 0.5;
    if(uv.x * uv.x + uv.y * uv.y < r * r){
        col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(4,2,0));
    }

    float rr =0.65 +0.65*sin(10.0+10.0*sin(iTime));
    if(uv.x * uv.x + uv.y * uv.y < rr * rr){
        col = vec3(0,0,0);
    }

    // Output to screen
    fragColor = vec4(col,1.1);


}
