// HLSL -> GLSL https://anteru.net/blog/2016/mapping-between-HLSL-and-GLSL/
#define float2   vec2
#define float3   vec3
#define float4   vec4
#define lerp     mix
#define atan2    atan
#define frac     fract
#define fmod     mod
#define float2x2 mat2     
#define mul(a, b) a*b 
#define texSampl 1.
//#define Texture2DSample(tex, texSampler, uv) texture(iChannel0, uv)
#define ddx dFdx
#define ddy dFdy
#define saturate(oo) clamp(oo, 0.0, 1.0)

float4 res (float2 uv, float t, float speed ){
    float3 col = float3(0., 0., 0.);
    float2 uv0 = uv * float2(3., .5);
    float a = 1.;
    t *= -speed;
    float t1 = t;
    float2 uv1 = uv0 + float2(0., .125 + t1);
    //float3 tex_1 = Texture2DSample(tex, texSampler, uv1).rgb;
    float3 tex_1 = texture(iChannel0, uv1).rgb;

    float t2 = t * 1.2;
    float2 uv2 = uv0 + float2(0., .125 + t2);
    //float3 tex_2 = Texture2DSample(tex, texSampler, uv2).rgb;
    float3 tex_2 = texture(iChannel0, uv2).rgb;

    float t3 = t * 1.35;
    float2 uv3 = uv0 + float2(0., .125 + t3);
    //float3 tex_3 = Texture2DSample(tex, texSampler, uv3).rgb;
    float3 tex_3 = texture(iChannel0, uv3).rgb;

    if (uv.x < .33) col += tex_1;
    else if (uv.x > 0.66) col += tex_3;
    else col += tex_2;
    if (uv.y < .33 || uv.y>.66) {
        a = 0.;
        col = float3(0.,0.,0.);
        }
    return float4(col, a);

}



void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;    
    float speed = -.5;
    fragColor = vec4(res(uv, iTime, speed));
}
