void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy / iResolution.xy)* 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;

    vec2 xr = uv;
    vec2 xg = uv;
    vec2 xb = uv;

    xr.y -= 0.5 * sin(4.*iTime);
    xg.y -= 0.5 * sin(4.*iTime+2.09);
    xb.y -= 0.5 * sin(4.*iTime+4.18);

    xr.x -= 0.5 * cos(1.*iTime);
    xg.x -= 0.5 * cos(1.*iTime+2.09);
    xb.x -= 0.5 * cos(1.*iTime+4.18);

    float dr = step(1.,length(xr) * 2.);
    float dg = step(1.,length(xg) * 2.);
    float db = step(1.,length(xb) * 2.);

    fragColor = vec4(dr,dg,db,0.0);

}
