void mainImage( out vec4 fragColor, in vec2 fragCoord )
{



   float zoom = 1.5*(1. + cos(iTime*3.141526/16.)/1.009);
   float xShift = 1.0 + (cos(iTime*3.141526/16.)*1.3);
   float yShift = 0.5*zoom + (cos(iTime*3.141526/16.)*0.025);

   float x = (fragCoord.x/iResolution.y*zoom)-xShift;
   x = (sin(iTime*3.141526/3.)/3.0)*x;
   float y = (fragCoord.y/iResolution.y*zoom)-yShift;
   y = (cos(iTime*3.141526/3.)/3.0)*y;


   float n = 24./(0.25 + log(1. + zoom));
   for(float i = 0.; i < n; i++){

       float yOld = y; 
       y = x*y*2.;
       x = (x*x) - (yOld*yOld);
       y = y + (fragCoord.y/iResolution.y*zoom)-yShift;
       x = x + (fragCoord.x/iResolution.x*zoom)-xShift;

   }

   fragColor = vec4((x+y*2.), ((x*2.)+y), 2.*(x+y), 1.0);

}
