float ToDegress(float f){
float PI = 3.141592653589793238462643383279502884197169;
return f = f * (PI/180.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float Scale = 0.05;
    float PointSize = 0.01;
    float ZoomSpeed = 10.;
    vec2 uv = fragCoord/iResolution.xy - .5;
    uv *= iTime * ZoomSpeed;
    uv.x *= iResolution.x/iResolution.y;
    vec3 col;
    for(float i =0.;i<iTime * 10. * ZoomSpeed;i++){
        float phi = ToDegress(137.5) * i;
        float r = i * Scale;
        vec2 points=vec2(r * cos(phi),r * sin(phi));
        if(length(uv - points) < PointSize)col.r = 1.;
        PointSize = i * 0.001;
    }

    fragColor = vec4(col,1.0);
}
