void mainImage( out vec4 fragColor, in vec2 uv )
{
    vec3 aColor = vec3(1.0, 0.0, 0.0);
    vec3 bColor = vec3(1.0, 1.0, 1.0);
    float squareSize = 50.0;
    float speedModifier = 1.5;

    float offset = iTime * squareSize * speedModifier;

    // Calculate the index of the current square
    int xIndex = int((uv.x + offset) / squareSize);
    int yIndex = int((uv.y + offset) / squareSize);

    // Check if the current pixel is inside a yellow square
    bool inAColorSquare = (xIndex % 2 == 0 && yIndex % 2 == 0) || (xIndex % 2 == 1 && yIndex % 2 == 1);

    vec3 col = inAColorSquare ? aColor : bColor;

    fragColor = vec4(col,1.0);
}
