precision mediump float;
#define PI 3.1415926;


float pix;

float plotSin(vec2 st, float k) {
  // st.x使用cos加点变化
  float pct = st.y - sin(cos(st.x + k) * 10. + iTime) / 10.;
  float pixs = pix * 40.0;
  return 1.0 - smoothstep(0.02 - pixs, 0.02+pixs, abs(pct));
}

// https://iquilezles.org/articles/palettes/
vec3 palette(float t) {
  vec3 a = vec3(0.186, 0.966, 0.731);
  vec3 b = vec3(0.199, 0.400, 0.604);
  vec3 c = vec3(1.432, 0.024, 0.755);
  vec3 d = vec3(0.048, 2.989, 2.952);

  float TWO_PI = 2.0 * PI;
  return a + b * cos(TWO_PI * (c * t + d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy / iResolution.y - vec2(0, 0.5);

  pix = 1. / iResolution.y;

  vec3 finc = vec3(0.0);
  vec3 color = palette(st.x - iTime * 0.8);


  for (float k=0.0;k<4.0;k+=1.0) {
    float pct = plotSin(st, k);
    finc += color * pct;
  }

  fragColor = vec4(finc, 1.0);
}
