void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;

    vec2 digitSize = vec2(0.12, 0.3);
    vec2 digitPos = vec2(-0.3, 0.0);
    float digitSpacing = 0.1;
    float digitLineWidth = 0.02;

    int number = 3; // Change this to display a different number

    vec4 segments[10];
    segments[0] = vec4(1.0, 1.0, 1.0, 1.0); // all segments
    segments[1] = vec4(0.0, 1.0, 1.0, 0.0); // segments b and c
    segments[2] = vec4(1.0, 1.0, 0.0, 1.0); // segments a, b, d, e, and g
    segments[3] = vec4(1.0, 1.0, 1.0, 0.0); // segments a, b, c, and d
    segments[4] = vec4(0.0, 1.0, 1.0, 0.0); // segments b, c, f, and g
    segments[5] = vec4(1.0, 0.0, 1.0, 1.0); // segments a, c, d, f, and g
    segments[6] = vec4(1.0, 0.0, 1.0, 1.0); // segments a, c, d, e, f, and g
    segments[7] = vec4(1.0, 1.0, 1.0, 0.0); // segments a, b, and c
    segments[8] = vec4(1.0, 1.0, 1.0, 1.0); // all segments
    segments[9] = vec4(1.0, 1.0, 1.0, 0.0); // segments a, b, c, d, f, and g

    for (int i = 0; i < 7; i++) {
        float x = digitPos.x + float(i) * (digitSize.x + digitSpacing);
        float y = digitPos.y;
        float segWidth = digitSize.x - 2.0 * digitLineWidth;
        float segHeight = digitSize.y * 0.25;
        float segX = x + digitLineWidth;
        float segY = y + digitSize.y * 0.75;

        float segmentValue = 0.0;
        if (i == 0) segmentValue = float(number / 1000);
        else if (i == 1) segmentValue = float((number / 100) % 10);
        else if (i == 2) segmentValue = float((number / 10) % 10);
        else if (i == 3) segmentValue = float(number % 10);

        for (int j = 0; j < 4; j++) {
            if (segments[int(segmentValue)][j] > 0.5) {
                float segTop = segY - segHeight * float(j);
            vec2 p1 = vec2(segX, segTop);
            vec2 p2 = vec2(segX + segWidth, segTop);
            vec2 p3 = vec2(segX + segWidth, segTop - segHeight);
            vec2 p4 = vec2(segX, segTop - segHeight);

            fragColor = mix(fragColor, vec4(1.0, 1.0, 1.0, 1.0), step(length(uv - p1), digitLineWidth));
            fragColor = mix(fragColor, vec4(1.0, 1.0, 1.0, 1.0), step(length(uv - p2), digitLineWidth));
            fragColor = mix(fragColor, vec4(1.0, 1.0, 1.0, 1.0), step(length(uv - p3), digitLineWidth));
            fragColor = mix(fragColor, vec4(1.0, 1.0, 1.0, 1.0), step(length(uv - p4), digitLineWidth));
        }
    }
}
}
