// Fork of "Swirled" by Kali. https://shadertoy.com/view/mtjSzh
// 2023-02-19 01:20:56

const float max_rad=.005;
const float it=25.;

mat2 rot(float a){
	float s=sin(a);
    float c=cos(a);
    return mat2(c,s,-s,c);
}

float hash(vec2 p)
{
    p*=1342.;
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

  float vignette(vec2 uv) {
      uv *=  1.0 - uv.yx;   //vec2(1.0)- uv.yx; -> 1.-u.yx; Thanks FabriceNeyret !

      float vig = uv.x*uv.y * 8.0; // multiply with sth for intensity

      vig = pow(vig, 0.25); // change pow for modifying the extend of the  vignette
      return vig;
  }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord/iResolution.xy;
	     uv.y = 1.-uv.y;
    vec2 uv2=floor(uv*50.)/50.;
	mat2 spin=rot(2.39996);
    vec2 p=vec2(0.,3.);
    vec3 res=vec3(0.);
    float ti=mod(iTime,10.);
    float rad_step=max_rad/it+hash(uv+ti)*.003;
	float rad=0.;
    //float vhs=step(.98,hash(uv.yy+ti))*(1.+sin(uv.y*5.+ti))*step(.5,hash(uv.xy+ti))*smoothstep(0.,1.,uv.y);
    vec4 col=texture(iChannel0,uv);
    for (float i=0.;i<it; i++) {
        rad+=rad_step;
        p*=spin;
        vec4 col=texture(iChannel0,uv+p*rad);
        res+=smoothstep(.2,1.,max(col.r,max(col.g,col.b)))*col.rgb;
    };
    res/=it;
    col.rgb=mix(length(col.rgb)*vec3(.5),col.rgb,.75);
    vec4 ff = vec4(col.rgb*.5+res,1.0);
    ff+=(hash(uv+(ti*20.))-.5)*.05;
    ff.g=min(ff.r*1.5,ff.g);
    ff.g=min(ff.b*1.5,ff.g);

    float k = (sin(iTime / 1.0) + 1.0)/4.0 + 0.75;

  ff -= vec4((1.-uv.y)*0.1*k,0,0,1.);
  ff -= (vec4(uv.y, uv.y * 0.8, uv.y, 1.)) / 8.0;

    ff*=vignette(uv);
    fragColor = ff;


}

#define fxrand floor(0.5)*.1456512
#define hash1 rnd(fxrand+.6546)
#define hash2 rnd(fxrand+1.1123)
#define hash8 rnd(fxrand+7.1376)
#define hash9 rnd(fxrand+8.9176)
#define hash10 rnd(fxrand+10.1076)
#define hash11 rnd(fxrand+11.1113)
#define hash12 rnd(fxrand+21.1196)
#define hash13 rnd(fxrand+41.1276)
#define hash15 rnd(fxrand+15.8246)
#define hash16 rnd(fxrand+16.8264)
#define hash17 rnd(fxrand+17.1824)
#define hash19 rnd(fxrand+19.2824)
#define hash20 rnd(fxrand+20.4814)

#define time iTime*.1
#define resolution iResolution


float det=.001;
vec3 pos;


float rnd(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    return fract(2.*p*p);
}

mat2 rot(float a){
    float s=sin(a),c=cos(a);
    return mat2(c,s,-s,c);
}


float fractal(vec3 p) {
  float depth = smoothstep(0., 6., -p.z + 5.);
  p *= .3;
  p = abs(2. - mod(p + vec3(0.4, 0.7, time), 4.));
  float ls = 0.;
  float c = 0.;
  for (int i = 0; i < 6; i++) {
    p = abs(p) / min(dot(p, p), 1.) - .9;
    float l = length(p);
    c += abs(l - ls);
    ls = l;
  }
  return smoothstep(0., 100., c) * 3.;
}

float de(vec3 p) {
    return length(p)-2.;
}

vec3 ldir;

vec3 color(vec3 p) {
    p.xz*=rot((p.y*(10.+hash2*1.)+time*20.)*sign(hash17-.5));
    float a=.3+hash8*1.;
    float b=.3+hash9*1.;
    vec3 col=abs(mix(sin(p*a/10.),cos(p*b/100.),clamp(p.y,-1.5,1.5)));
    col.rb*=rot(hash10*2.);
    col.rg*=rot(hash11*5.1);
    col.gb*=rot(hash12*39.9);
    col=abs(col);
    return col;
}

vec3 shade(vec3 p, vec3 dir, vec3 n) {
    float amb=.3;
    float dif=max(0.,dot(ldir,n))*.8;
    vec3 col=color(pos)*1.;
    return (amb+dif)*col;

}

vec3 march(vec3 from, vec3 dir) {
    ldir=normalize(vec3(hash19-.5,hash20-.5,-.5));
    ldir=normalize(vec3(0.,hash20-.5,-.5));
    float td=0.,d=0.,ref=0.,maxdist=45.;
    vec3 col=vec3(0.),colref=col,p=from,g=col;
    float fg = fractal(p + dir*5.) * .55;
    for(int i=0; i<200; i++){
        p+=d*dir;
        d=de(p);
        if (d<det&&ref>0.||td>maxdist) break;
        if (d<det&&ref<1.) {
            ref=1.;
            vec3 n=normalize(p);
            colref+=shade(p,dir,n);
            dir=reflect(dir,n);
            p+=dir*.1;
        }
        g+=.1/(.1+d);
        td+=d;
    }
    vec3 n=normalize(p);
    if (d>det) {
        if (ref>.5) maxdist*=.5;
        td=maxdist;
        p=dir*maxdist;
    } else {
        col=shade(p,dir,n);
    }
    p.xy*=rot(hash16*3.);
    vec3 di=dir;
    di.xz*=rot(.5);
    di.xy*=rot(.5);
    vec3 back=color(di);
    back=mix(vec3(length(back)*.5),back,.5);
    back=sqrt(abs(back)*.45);
    back+=fractal(p*.15)*back;
    back+=pow(max(0.,dot(dir,ldir)),20.)*.5;
    float r=.3+hash13*.6;
    col=back;
    col+=fg*1.5;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;
    vec2 uv2 = fragCoord/iResolution.xy-.5;
    vec3 from=vec3(.0,.0,-4.-5.*hash15);
    vec3 dir=normalize(vec3(uv,1.));
    from.xz*=rot(1.+time*2.+1.);
    dir.xz*=rot(1.+time*2.+1.);
    vec3 col = march(from,dir);
    vec3 buf = texture(iChannel0,fragCoord/iResolution.xy).rgb;
    col=buf*.75+col*.25;
    col*=smoothstep(.7,.47,abs(uv2.x));
    col*=smoothstep(.7,.47,abs(uv2.y));



    fragColor = vec4(col,1.)*step(.5,8.);
}
