void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 p = uv-vec2(0.3,0.6);
    vec2 p2 = uv-vec2(0.3*exp(sin(iTime)),0.5);


    vec3 col = vec3(1.0, 0.3 , 0.6);

    float r = 0.2 + 0.015*exp(sin(iTime)) * cos(atan(p.y,p.x)*5.0); 
    //float r = 0.2 + 0.1 *cos (atan(p.y,p.x)*10.0);

    col = col * smoothstep(r,r + 0.06,length(p)) ; 
    vec3 col2 = vec3(1.0,0.6,0.3) * smoothstep(0.2,0.202,length(p2)) ; 






    fragColor = vec4(col,1.0);
    fragColor = vec4(col2,1.0+sin(iTime)) + vec4(col+sin(iTime*4.0),col) * vec4(col,col) + vec4(col2,1.0)+sin(iTime);
  //  fragColor = vec4(vec3(1.0,1.0,0.5),col2+col) ;

}
