void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float r = length(uv);
    float a = atan(uv.y, uv.x);

    vec3 color = vec3(1.0, 1.0, 0.9);

    // Draw the face
    if (r > 0.8)
    {
        color = vec3(0.0, 0.0, 0.0);
    }

    // Draw the eyes
    float eyeState = step(0.5, sin(iTime * 10.0 + length(uv - vec2(0.3, 0.3))) * 0.5 + 0.5);
    if (length(uv - vec2(0.3, 0.3)) < 0.1 * eyeState || length(uv - vec2(-0.3, 0.3)) < 0.1 * eyeState)
    {
        color = vec3(0.0, 0.0, 0.0);
    }

    // Draw the mouth
    if (uv.y < -0.6 && length(uv - vec2(0.0, -0.7)) < 0.1 + 0.05 * sin(iTime * 5.0))
    {
        color = vec3(0.0, 0.0, 0.0);
    }

    // Add shading to the face
    color -= vec3(r * 0.2, r * 0.2, r * 0.2);

    fragColor = vec4(color, 1.0);
}
